import React from 'react';
import {BrowserRouter as Router, Switch,Route} from 'react-router-dom';


import 'bootstrap/dist/css/bootstrap.css';
/*import 'font-awesome/css/font-awesome.min.css';*/
import 'assets/scss/zest-admin.css';
import 'assets/fonts/simple-line-icons.css';


import PublicRoute from './routes/PublicRoute';
import PrivateRoute from './routes/PrivateRoute';

import LoginPage from '../src/layouts/LoginPage';
import GeneralLayout from './layouts/General';
import BlogLayout from './layouts/Blog.jsx';

import indexRoutes from './routes/index';
import { AuthContext } from '../src/shared/context/auth-context';
import { useAuth } from '../src/shared/hooks/auth-hook';


const App = () => {

  const { token, login, logout, userId } = useAuth();


  const [collapsed, setcollapsed] = useState(false)
    return (
        <AuthContext.Provider
        value={{ 
          isLoggedIn: !!token, 
          token : token,
          login: login, 
          userId : userId,
           logout: logout
           }}
      >
        <Router>
            <Switch>
                <PublicRoute exact path="/login">
                <LoginPage />
                </PublicRoute>
                <PublicRoute exact path="/register">
                <h1>login</h1>
                </PublicRoute>
                <PublicRoute exact path="/callback">
                    <h1>Callback</h1>
                </PublicRoute>
               { indexRoutes.map((prop,key) => {
                return ( 
                <PrivateRoute
                    key={key}
                    component={prop.component}
                     path={prop.path} >
                     </PrivateRoute>
                      )
                 })
               }
            </Switch>
        </Router>
        </AuthContext.Provider>
    );
};

export default App;
